import React, { Component } from 'react'
import Radium from 'radium'
import './Modal.css'

const styles = {
  background: {
    background: 'rgba(0, 0, 0, 0.5)',
    position: 'fixed',
    top: 0,
    left: 0,
    bottom: 0,
    right: 0,
  },
  modal: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    outline: 0,
    width: '400px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '40px auto',
    opacity: 0,
  },
  boxDialog: {
    width: '100%',
    backgroundColor: '#fefefe',
    boxShadow: '0 3px 9px rgba(0, 0, 0, 0.5)',
    zIndex: 1,
    padding: '40px',
  },
  fadeIn: {
    opacity: 0,
    transition: 'opacity linear 1s',
  },
  fadeOut: {
    opacity: 1,
    transition: 'opacity linear 1s',
  }
}

export default class Modal extends Component {

  state = {
    unmounting: false,
    fadeType: null
  }

  componentDidMount = () => {
    setTimeout(() => this.setState({ fadeType: 'In' }), 0)
  }

  transitionEnd = e => {
    console.log(e)
    if (!this.state.unmounting) return;

    if (this.state.fadeType === 'out') {
      this.props.onClose()
    }
  }

  handleClick = e => {
    this.setState({ fadeType: "Out", unmounting: true })
  }

  render = () => {
    return (
      <div
        onTransitionEnd={this.transitionEnd}
        style={[styles.modal, styles[`fade${this.state.fadeType}`]]}
      >
        <div
          style={styles.boxDialog}
        >
          Modal
          <button onClick={this.handleClick}>Close</button>
        </div>
        <div 
          className='background'
        />
      </div>
    )
  }
}
