import React, { Component } from 'react'
import Radium from 'radium'
import { render } from 'react-dom'

const animations = {
  fadeInOut: {
    mount: Radium.keyframes({
      '0%': { opacity: '0' },
      '100%': { opacity: '1' }
    }),
    unmount: Radium.keyframes({
      '0%': { opacity: '1' },
      '100%': { opacity: '0' }
    })
  }
}

export default class Modal extends Component {

  state = {
    isMounted: false
