import React, { Component } from 'react'
import Radium from 'radium'
import './Modal.css'

const styles = {
  modal: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    outline: 0,
    width: '400px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '40px auto',
    opacity: 0,
  },
  boxDialog: {

  },
  fadeIn: {

  },
  fadeOut: {

  }
}

export default class Modal extends Component {

  state = {
    unmounting: false,
    fadeType: null
  }

  componentDidMount = () => {
    setTimeout(() => this.setState({ fadeType: 'in' }), 0)
  }

  transitionEnd = e => {
    console.log(e)
    if (!this.state.unmounting) return;

    if (this.state.fadeType === 'out') {
      this.props.onClose()
    }
  }

  handleClick = e => {
    this.setState({ fadeType: "out", unmounting: true })
  }

  render = () => {
    return (
      <div
        onTransitionEnd={this.transitionEnd}
        className={`fade-${this.state.fadeType}`}

      >
        <div
          style={styles.boxDialog}
        >
          Modal
          <button onClick={this.handleClick}>Close</button>
        </div>
        <div 
          className='background'
        />
      </div>
    )
  }
}
